#![allow(non_camel_case_types)]

use libc::{c_uchar, c_void};

/// Regex wraps a D regular expression
pub struct Regex {
    re: *mut d_regex,
}

unsafe impl Send for Regex {}

impl Drop for Regex {
    fn drop(&mut self) {
        unsafe {
            d_phobos_regex_free(self.re);
            rt_term();
        }
    }
}

#[derive(Debug)]
pub struct Error(());

impl Regex {
    pub fn new(pattern: &str) -> Result<Regex, Error> {
        unsafe {
            rt_init();

            Ok(Regex { re: d_phobos_regex_new(pattern.into()) })
        }
    }

    pub fn is_match(&self, text: &str) -> bool {
        unsafe { d_phobos_regex_is_match(self.re, text.into()) }
    }

    pub fn find_iter<'r, 't>(&'r self, text: &'t str) -> FindMatches<'r, 't> {
        FindMatches { re: self, text: text, last_end: 0, last_match: None }
    }

    fn find_at(&self, text: &str, start: usize) -> Option<(usize, usize)> {
        let (mut s, mut e): (usize, usize) = (0, 0);
        let matched = unsafe {
            d_phobos_regex_find_at(self.re, text.into(), start, &mut s, &mut e)
        };
        if matched {
            Some((s, e))
        } else {
            None
        }
    }
}

pub struct FindMatches<'r, 't> {
    re: &'r Regex,
    text: &'t str,
    last_end: usize,
    last_match: Option<usize>,
}

impl<'r, 't> Iterator for FindMatches<'r, 't> {
    type Item = (usize, usize);

    fn next(&mut self) -> Option<(usize, usize)> {
        match self.re.find_at(self.text, self.last_end) {
            None => None,
            Some((s, e)) => {
                self.last_end = e;
                Some((s, e))
            }
        }
    }
}

type d_regex = c_void;

#[repr(C)]
struct d_string {
    len: usize,
    text: *const c_uchar,
}

impl<'a> From<&'a str> for d_string {
    fn from(s: &'a str) -> d_string {
        d_string { len: s.len(), text: s.as_ptr() }
    }
}

extern "C" {
    fn rt_init() -> i32;
    fn rt_term() -> i32;
    fn d_phobos_regex_new(s: d_string) -> *mut d_regex;
    fn d_phobos_regex_free(r: *mut d_regex);
    fn d_phobos_regex_is_match(r: *mut d_regex, s: d_string) -> bool;
    fn d_phobos_regex_find_at(
        r: *mut d_regex,
        s: d_string,
        start: usize,
        match_start: *mut usize,
        match_end: *mut usize,
    ) -> bool;
}

use onig;

pub struct Regex(onig::Regex);

unsafe impl Send for Regex {}

impl Regex {
    pub fn new(pattern: &str) -> Result<Self, onig::Error> {
        onig::Regex::new(pattern).map(Regex)
    }

    pub fn is_match(&self, text: &str) -> bool {
        // Gah. onig's is_match function is anchored, but find is not.
        self.0
            .search_with_options(
                text,
                0,
                text.len(),
                onig::SearchOptions::SEARCH_OPTION_NONE,
                None,
            )
            .is_some()
    }

    pub fn find_iter<'r, 't>(
        &'r self,
        text: &'t str,
    ) -> onig::FindMatches<'r, 't> {
        self.0.find_iter(text)
    }
}

#![allow(non_snake_case)]

use std::ffi::{CStr, CString};
use std::fmt;
use std::ptr;

use libc::{c_char, c_int, c_void};
use libpcre_sys::{
    pcre, pcre_compile, pcre_exec, pcre_extra, pcre_free, pcre_free_study,
    pcre_study, PCRE_ERROR_NOMATCH, PCRE_NO_UTF8_CHECK, PCRE_UTF8,
};

const PCRE_UCP: c_int = 0x20000000;
const PCRE_STUDY_JIT_COMPLETE: c_int = 0x0001;

// We use libpcre-sys directly because the pcre crate has unavoidable
// performance problems in its core matching routines. (e.g., It always
// allocates an ovector.)
pub struct Regex {
    code: *mut pcre,
    extra: *mut pcre_extra,
}

unsafe impl Send for Regex {}

impl Drop for Regex {
    fn drop(&mut self) {
        unsafe {
            pcre_free_study(self.extra);
            pcre_free(self.code as *mut c_void);
        }
    }
}

pub struct Error {
    msg: String,
    offset: c_int,
}

impl Regex {
    pub fn new(pattern: &str) -> Result<Regex, Error> {
        let pattern = CString::new(pattern.to_owned()).unwrap();
        let mut errptr: *const c_char = ptr::null();
        let mut erroffset: c_int = 0;
        let code = unsafe {
            pcre_compile(
                pattern.as_ptr(),
                PCRE_UCP | PCRE_UTF8,
                &mut errptr,
                &mut erroffset,
                ptr::null(),
            )
        };
        if code.is_null() {
            let msg =
                unsafe { CStr::from_ptr(errptr).to_str().unwrap().to_owned() };
            return Err(Error { msg: msg, offset: erroffset });
        }

        let extra =
            unsafe { pcre_study(code, PCRE_STUDY_JIT_COMPLETE, &mut errptr) };
        if extra.is_null() {
            if errptr.is_null() {
                panic!("unexpected error. Maybe JIT support isn't enabled?");
            }
            let msg =
                unsafe { CStr::from_ptr(errptr).to_str().unwrap().to_owned() };
            return Err(Error { msg: msg, offset: 0 });
        }
        Ok(Regex { code: code, extra: extra })
    }

    pub fn is_match(&self, text: &str) -> bool {
        self.find_at(text, 0).is_some()
    }

    pub fn find_iter<'r, 't>(&'r self, text: &'t str) -> FindMatches<'r, 't> {
        FindMatches { re: self, text: text, last_match_end: 0 }
    }

    fn find_at(&self, text: &str, start: usize) -> Option<(usize, usize)> {
        const OVEC_SIZE: usize = 15 * 3; // hopefully enough for benchmarks?
        let mut ovec: [c_int; OVEC_SIZE] = [0; OVEC_SIZE];
        let err = unsafe {
            pcre_exec(
                self.code,
                self.extra,
                text.as_ptr() as *const i8,
                text.len() as c_int,
                start as c_int,
                PCRE_NO_UTF8_CHECK,
                ovec.as_mut_ptr(),
                OVEC_SIZE as c_int,
            )
        };
        if err == PCRE_ERROR_NOMATCH {
            None
        } else if err < 0 {
            panic!("unknown error code: {:?}", err)
        } else {
            Some((ovec[0] as usize, ovec[1] as usize))
        }
    }
}

pub struct FindMatches<'r, 't> {
    re: &'r Regex,
    text: &'t str,
    last_match_end: usize,
}

impl<'r, 't> Iterator for FindMatches<'r, 't> {
    type Item = (usize, usize);

    fn next(&mut self) -> Option<(usize, usize)> {
        match self.re.find_at(self.text, self.last_match_end) {
            None => None,
            Some((s, e)) => {
                self.last_match_end = e;
                Some((s, e))
            }
        }
    }
}

impl fmt::Debug for Error {
    fn fmt(&self, f: &mut fmt::Formatter) -> fmt::Result {
        write!(f, "PCRE error at {:?}: {}", self.offset, self.msg)
    }
}

#![allow(non_camel_case_types)]

use std::fmt;
use std::ptr;
use std::str;

use libc::{c_int, c_void, size_t, uint32_t, uint8_t};

pub struct Regex {
    code: *mut code,
    match_data: *mut match_data,
    ovector: *mut size_t,
}

unsafe impl Send for Regex {}

impl Drop for Regex {
    fn drop(&mut self) {
        unsafe {
            pcre2_match_data_free_8(self.match_data);
            pcre2_code_free_8(self.code);
        }
    }
}

pub struct Error {
    code: c_int,
    offset: size_t,
}

impl Regex {
    pub fn new(pattern: &str) -> Result<Regex, Error> {
        let mut error_code: c_int = 0;
        let mut error_offset: size_t = 0;
        let code = unsafe {
            pcre2_compile_8(
                pattern.as_ptr(),
                pattern.len(),
                // PCRE2 can get significantly faster in some cases depending
                // on the permutation of these options (in particular, dropping
                // UCP). We should endeavor to have a separate "ASCII compatible"
                // benchmark.
                PCRE2_UCP | PCRE2_UTF,
                &mut error_code,
                &mut error_offset,
                ptr::null_mut(),
            )
        };
        if code.is_null() {
            return Err(Error { code: error_code, offset: error_offset });
        }
        let err = unsafe { pcre2_jit_compile_8(code, PCRE2_JIT_COMPLETE) };
        if err < 0 {
            panic!("pcre2_jit_compile_8 failed with error: {:?}", err);
        }
        let match_data = unsafe {
            pcre2_match_data_create_from_pattern_8(code, ptr::null_mut())
        };
        if match_data.is_null() {
            panic!("could not allocate match_data");
        }
        let ovector = unsafe { pcre2_get_ovector_pointer_8(match_data) };
        if ovector.is_null() {
            panic!("could not get ovector");
        }
        Ok(Regex { code: code, match_data: match_data, ovector: ovector })
    }

    pub fn is_match(&self, text: &str) -> bool {
        self.find_at(text, 0).is_some()
    }

    pub fn find_iter<'r, 't>(&'r self, text: &'t str) -> FindMatches<'r, 't> {
        FindMatches { re: self, text: text, last_match_end: 0 }
    }

    fn find_at(&self, text: &str, start: usize) -> Option<(usize, usize)> {
        // The man pages for PCRE2 say that pcre2_jit_match is the fastest
        // way to execute a JIT match because it skips sanity checks. We also
        // explicitly disable the UTF-8 validity check, but it's probably not
        // necessary.
        let err = unsafe {
            pcre2_jit_match_8(
                self.code,
                text.as_ptr(),
                text.len(),
                start,
                PCRE2_NO_UTF_CHECK,
                self.match_data,
                ptr::null_mut(),
            )
        };
        if err == PCRE2_ERROR_NOMATCH {
            None
        } else if err < 0 {
            panic!("unknown error code: {:?}", err)
        } else {
            Some(unsafe { (*self.ovector, *self.ovector.offset(1)) })
        }
    }
}

pub struct FindMatches<'r, 't> {
    re: &'r Regex,
    text: &'t str,
    last_match_end: usize,
}

impl<'r, 't> Iterator for FindMatches<'r, 't> {
    type Item = (usize, usize);

    fn next(&mut self) -> Option<(usize, usize)> {
        match self.re.find_at(self.text, self.last_match_end) {
            None => None,
            Some((s, e)) => {
                self.last_match_end = e;
                Some((s, e))
            }
        }
    }
}

impl fmt::Debug for Error {
    fn fmt(&self, f: &mut fmt::Formatter) -> fmt::Result {
        const BUF_LEN: size_t = 256;
        let mut buf = [0; BUF_LEN];
        let len = unsafe {
            pcre2_get_error_message_8(self.code, buf.as_mut_ptr(), BUF_LEN)
        };
        if len < 0 {
            write!(
                f,
                "Unknown PCRE error. (code: {:?}, offset: {:?})",
                self.code, self.offset
            )
        } else {
            let msg = str::from_utf8(&buf[..len as usize]).unwrap();
            write!(f, "error at {:?}: {}", self.offset, msg)
        }
    }
}

// PCRE2 FFI. We only wrap the bits we need.

const PCRE2_UCP: uint32_t = 0x00020000;
const PCRE2_UTF: uint32_t = 0x00080000;
const PCRE2_NO_UTF_CHECK: uint32_t = 0x40000000;
const PCRE2_JIT_COMPLETE: uint32_t = 0x00000001;
const PCRE2_ERROR_NOMATCH: c_int = -1;

type code = c_void;

type match_data = c_void;

type compile_context = c_void; // unused

type general_context = c_void; // unused

type match_context = c_void; // unused

extern "C" {
    fn pcre2_compile_8(
        pattern: *const uint8_t,
        len: size_t,
        options: uint32_t,
        error_code: *mut c_int,
        error_offset: *mut size_t,
        context: *mut compile_context,
    ) -> *mut code;

    fn pcre2_code_free_8(code: *mut code);

    fn pcre2_match_data_create_from_pattern_8(
        code: *const code,
        context: *mut general_context,
    ) -> *mut match_data;

    fn pcre2_match_data_free_8(match_data: *mut match_data);

    fn pcre2_get_ovector_pointer_8(match_data: *mut match_data)
        -> *mut size_t;

    fn pcre2_jit_compile_8(code: *const code, options: uint32_t) -> c_int;

    fn pcre2_jit_match_8(
        code: *const code,
        subject: *const uint8_t,
        length: size_t,
        startoffset: size_t,
        options: uint32_t,
        match_data: *mut match_data,
        match_context: *mut match_context,
    ) -> c_int;

    fn pcre2_get_error_message_8(
        error_code: c_int,
        buf: *mut uint8_t,
        buflen: size_t,
    ) -> c_int;
}

#![allow(non_camel_case_types)]

use libc::{c_int, c_uchar, c_void};

/// Regex wraps an RE2 regular expression.
///
/// It cannot be used safely from multiple threads simultaneously.
pub struct Regex {
    re: *mut re2_regexp,
}

unsafe impl Send for Regex {}

impl Drop for Regex {
    fn drop(&mut self) {
        unsafe {
            re2_regexp_free(self.re);
        }
    }
}

#[derive(Debug)]
pub struct Error(());

impl Regex {
    pub fn new(pattern: &str) -> Result<Regex, Error> {
        unsafe { Ok(Regex { re: re2_regexp_new(pattern.into()) }) }
    }

    pub fn is_match(&self, text: &str) -> bool {
        unsafe {
            re2_regexp_match(self.re, text.into(), 0, text.len() as c_int)
        }
    }

    pub fn find_iter<'r, 't>(&'r self, text: &'t str) -> FindMatches<'r, 't> {
        FindMatches { re: self, text: text, last_end: 0, last_match: None }
    }

    fn find_at(&self, text: &str, start: usize) -> Option<(usize, usize)> {
        let (mut s, mut e): (c_int, c_int) = (0, 0);
        let matched = unsafe {
            re2_regexp_find(
                self.re,
                text.into(),
                start as c_int,
                text.len() as c_int,
                &mut s,
                &mut e,
            )
        };
        if matched {
            Some((s as usize, e as usize))
        } else {
            None
        }
    }
}

pub struct FindMatches<'r, 't> {
    re: &'r Regex,
    text: &'t str,
    last_end: usize,
    last_match: Option<usize>,
}

// This implementation is identical to the one Rust uses, since both Rust's
// regex engine and RE2 handle empty matches in the same way.
impl<'r, 't> Iterator for FindMatches<'r, 't> {
    type Item = (usize, usize);

    fn next(&mut self) -> Option<(usize, usize)> {
        fn next_after_empty(text: &str, i: usize) -> usize {
            let b = match text.as_bytes().get(i) {
                None => return text.len() + 1,
                Some(&b) => b,
            };
            let inc = if b <= 0x7F {
                1
            } else if b <= 0b110_11111 {
                2
            } else if b <= 0b1110_1111 {
                3
            } else {
                4
            };
            i + inc
        }

        if self.last_end > self.text.len() {
            return None;
        }
        let (s, e) = match self.re.find_at(self.text, self.last_end) {
            None => return None,
            Some((s, e)) => (s, e),
        };
        assert!(s >= self.last_end);
        if s == e {
            // This is an empty match. To ensure we make progress, start
            // the next search at the smallest possible starting position
            // of the next match following this one.
            self.last_end = next_after_empty(&self.text, e);
            // Don't accept empty matches immediately following a match.
            // Just move on to the next match.
            if Some(e) == self.last_match {
                return self.next();
            }
        } else {
            self.last_end = e;
        }
        self.last_match = Some(self.last_end);
        Some((s, e))
    }
}

